import * as React from "react";
import styles from './ProfileStatus.module.css'

class ProfileStatus extends React.Component {

    state = {
        editMode:false,
        status:this.props.status
    }

    changeEditMode  = () =>{

        this.setState({
            editMode:!this.state.editMode
        })
        props.postUserStatusThunkCreator(this.state.status)
    }

    render() {
        return <div className={styles.profileStatus}>
            {this.state.editMode ? <input  autoFocus={true} onBlur={this.changeEditMode} value={this.state.status}/>:
            <div  onDoubleClick={this.changeEditMode} >{this.state.status}</div> }
        </div>
    }
}

export default ProfileStatus;
