import {setAuthUserThunkCreator} from "./auth-reducer";

export const SET_INITIALIZED = 'SET-INITIALIZED'


let initialState ={
   initialized:false
}

let appReducer = (state = initialState, action) =>{
    switch (action.type){
        case SET_INITIALIZED:{
            return {
                ...state,
                initialized: true
            }
        }
        default:
            return state;
    }
}

export let initializeSuccess = () =>({
    type: SET_INITIALIZED
})

export let initializeApp = () => (dispatch) =>{
    let dispatchPromise = dispatch(setAuthUserThunkCreator());
    dispatchPromise.then(()=>{
        dispatch(initializeSuccess());
    })
}

export  default appReducer;
