import classes from './MyPosts.module.css'
import Post from './Post/Post'
import * as React from "react";


let addPostActionCreator = () => {

    return {
        type: 'ADD-POST'
    }
}

let updateNewPostTextActionCreator = (newText) => {
    return {
        type: 'UPDATE-NEW-POST-TEXT',
        newText :newText
    }
}


const MyPosts = (props) =>
{
    let posts = props.posts.map((post) => <Post  message = {post.message} likes_count = {post.likes_count}/>)

    let newPostElement = React.createRef();
    let addPost = () => {

       props.dispatch(addPostActionCreator());
    }

    let onPostChange = () =>{


        props.dispatch(updateNewPostTextActionCreator(newPostElement.current.value))
    }

    return(
        <div className={classes.postBlock}>
          my posts
            <div>
                <div> <textarea ref = {newPostElement} value={props.post_text} onChange={onPostChange}/></div>
                <div>     <button onClick={addPost}>Add Post</button></div>
            </div>
          <div className= {classes.posts}>
            new posts
              {posts}
          </div>
        </div>
    );
}

export default MyPosts;
