import classes from './MyPosts.module.css'
import Post from './Post/Post'
import * as React from "react";
import {addPostActionCreator, updateNewPostTextActionCreator} from "../../../redux/profile-reducer";
import AddPostForm from "./AddPostForm/AddPostForm";


class MyPosts extends React.PureComponent {

/*    shouldComponentUpdate(nextProps, nextState) {
        return nextProps!= this.props || nextState != this.state
    }*/

    render() {

        let posts = this.props.posts.map((post) => <Post message={post.message} likes_count={post.likes_count}/>)

        let newPostElement = React.createRef();
        let addPost = () => {

            this.props.addPost();
        }

        let onPostChange = () => {
            this.props.updateNewPostText(newPostElement.current.value);
        }

        return (
            <div className={classes.postBlock}>
                my posts
                <div>
                    {/*  <div> <textarea ref = {newPostElement} value={props.newPostText} onChange={onPostChange}/></div>
                <div>     <button onClick={addPost}>Add Post</button></div>*/}
                    <AddPostForm addPost={this.props.addPost}
                                 updateNewPostText={this.props.updateNewPostText}></AddPostForm>
                </div>
                <div className={classes.posts}>
                    new posts
                    {posts}
                </div>
            </div>
        );
    }
}

export default MyPosts;
