import ProfileStatusWithHooks from "./ProfileStatus/ProfileStatusWithHooks";
import Contact from "./Contact/Contact";
import {useState} from "react";
import {Input} from "../../../common/FormControls/FormControls";

const ProfileData = (props) => {
    let [editMode, setEditMode] = useState(false);
    let deactivateEditMode = () =>{
        setEditMode(false);

    }
    return (<div>
            <div>
                {props.profile.fullName}
            </div>
            <div>

                <b>Status:</b><ProfileStatusWithHooks status={props.status} postUserStatus={props.postUserStatus}
                                        disable={props.disable}/>
            </div>
            <div>
                <b>About me:</b>editMode?{props.profile.aboutMe}:
                {<Input/>}
            </div>
            <div>

                {
                    editMode?Object.keys(props.profile.contacts).map(k => {
                            return(<span>
                                <Contact key={k} title={k} link={''}/><Input/></span>)
                        }):
                        Object.keys(props.profile.contacts).map(k => {
                                        return(
                                        <Contact key={k} title={k} link={props.profile.contacts[k]}/>)
                                    })
                }
            </div>
            <div>
                {
                    props.isOwner && !editMode ? <button onClick={()=>{setEditMode(true)}
                    }>Edit Profile Description</button> : ''}
                {
                    props.isOwner && editMode ? <button onClick={deactivateEditMode
                    }>Edit Profile Description</button> : ''}

            </div>
        </div>
    )
}

export default ProfileData
