import * as React from "react";
import styles from "./Users.module.css";
import userAvatar from "../../assets/images/1.jpg";
import * as axios from "axios";


class Users extends React.Component {


    componentDidMount() {
        axios.get('https://social-network.samuraijs.com/api/1.0/users').then(response => {

            this.props.setUsers(response.data.items);
        })
    }

    render() {

        let pageCount = this.props.totalUsersCount/this.props.pageSize;
        return (

            <div>
                <span>1 </span>
                <span className={styles.selectedPage}>2 </span>
                <span>3 </span>
                <span>4 </span>
                <span>5 </span>

                {
                    this.props.users.map((u) => <div key={u.id}>
            <span>
                <div>
                    <img className={styles.smallUsersAvatar}
                         src={u.photos.small != null ? u.photos.small : userAvatar}></img>
                </div>
                <div>
                    {u.followed ? <button onClick={() => {
                        this.props.followChanged(u.id)
                    }}>Followed</button> : <button onClick={() => {
                        this.props.followChanged(u.id)
                    }}>Unfollowed</button>}
                </div>
            </span>
                            <span>
                    <div>{u.name}</div><div>{u.status}</div>
                    <div>Ukraine</div><div>Odessa</div>
            </span>
                        </div>
                    )
                }
            </div>
        )
    }
}

export default Users;
