export const SET_USER_DATA = 'SET-USER-DATA'


let initalState ={
    userID:null,
    email:null,
    login:null
}

let authReducer = (state, action) =>{
    switch (action.type){
        case SET_USER_DATA:{
            return {
                ...state,
                ...action.data
            }
        }
        default:
            return state;
    }
}

export const setUserData = (userID, email,login) =>({type:SET_USER_DATA, data:{userID,email,login}})

export  default authReducer;
