            {
                id: 1, followed: true, fullName: 'Eugeniy', status: 'Hey!',
                location: {
                    city: 'Odessa',
                    country: 'Ukraine'
                },
                photoUrl: 'https://st3.depositphotos.com/15648834/17930/v/600/depositphotos_179308454-stock-illustration-unknown-person-silhouette-glasses-profile.jpg'
            },
            {
                id: 2, followed: false, fullName: 'Sasha', status: 'Igor ne ochen :)!',
                location: {
                    city: 'Minsk',
                    country: 'Belarus'
                },
                photoUrl: 'https://st3.depositphotos.com/15648834/17930/v/600/depositphotos_179308454-stock-illustration-unknown-person-silhouette-glasses-profile.jpg'
            },
            {
                id: 3, followed: true, fullName: 'Igor', status: 'Vse govno!',
                location: {
                    city: 'Dublin',
                    country: 'Ierland'
                },
                photoUrl: 'https://st3.depositphotos.com/15648834/17930/v/600/depositphotos_179308454-stock-illustration-unknown-person-silhouette-glasses-profile.jpg'
            }
        ])*/
    }
    return (
        <div>
            {
                props.users.map((u) => <div key={u.id}>
            <span>
                <div>
                    <img src={u.photoUrl}></img>
                </div>
                <div>
                    {u.followed ? <button onClick={() =>{props.followChanged(u.id)}}>Followed</button> : <button onClick={() =>{props.followChanged(u.id)}}>Unfollowed</button>}
                </div>
            </span>
                        <span>
                    <div>{u.fullName}</div><div>{u.status}</div>
                    <div>{u.location.country}</div><div>{u.location.city}</div>
            </span>
                    </div>
                )
            }
        </div>
    )
}

export default Users;
