import styles from "./ProfileInfo.module.css"
import Preloader from "../../common/Preloader/Preloader";
import ProfileStatus from "./ProfileStatus/ProfileStatus";
import ProfileStatusWithHooks from "./ProfileStatus/ProfileStatusWithHooks";
import userPhoto from './../../../assets/images/1.png'

const ProfileInfo = (props) => {
    if(!props.profile)
    {
        return <Preloader />
    }
    else {

        let onUserPhototAvatarChange = (e) =>{
            if(e.files.length>0)
            {
                props.savePhoto(e.files[0])
            }
        }

        let contactsKeys =  Object.keys(props.profile.contacts);
        let contactsValues =  Object.values(props.profile.contacts);
        return (
            <div>
               {/* <div>
                    <img
                        src='https://helpx.adobe.com/content/dam/help/en/stock/how-to/visual-reverse-image-search/jcr_content/main-pars/image/visual-reverse-image-search-v2_intro.jpg'/>
                </div>*/}
                <div className={styles.descriptionBlock}>
                    <div className={styles.profileAvatar}>
                    <img src={props.profile.photos.large || userPhoto} />
                    </div>
                    <div>
                        {
                            props.isOwner?<input type={'file'} onChange={onUserPhototAvatarChange}/>:''
                        }
                    </div>
                    <div>
                        {props.profile.fullName}
                    </div>
                    <div>
                        {/*<ProfileStatus status = {props.status} postUserStatus ={props.postUserStatus} disable={props.disable}/>*/}
                            <ProfileStatusWithHooks status = {props.status} postUserStatus ={props.postUserStatus} disable={props.disable}/>
                    </div>
                    <div>
                        <span>
                        {
                            contactsKeys.map(k =>{
                                return<div>{k}: <a href = {contactsValues[contactsKeys.indexOf(k)]}>{contactsValues[contactsKeys.indexOf(k)]}</a></div>
                            })

                        }
                        </span>
                    </div>
                </div>
            </div>
        );
    }
}

export default ProfileInfo;
