import style from './Dialogs.module.css';
/*import DialogItem from "./DialogItem/DialogItem";*/
import MessageItem from "./MessageItem/MessageItem";
import {NavLink} from "react-router-dom";


const DialogItem = (props) => {
    return <div>
        <NavLink to={"/dialogs/" + props.profile_id}>{props.name}</NavLink>
    </div>
}





let DialogsProfiles = [
    {name: 'Alexey', profile_id: '1'},
    {name: 'Sasha', profile_id: '2'},
    {name: 'Anton', profile_id: '3'},
    {name: 'Valera', profile_id: '4'},
]

let DialogsElements = DialogsProfiles.map((profile) => <DialogItem name={profile.name} profile_id={profile.profile_id}/>);

let DialogMessages = [
    {message : 'hi!'},
    {message : 'hello!'},
    {message : 'how ar u?'},
    {message : 'good! what bout u?'}
]

let MessagesElements = DialogMessages.map((message) =>{
    return     <MessageItem  message={message.message}/>
})


const Dialogs = () => {
    return (
        <div className={style.dialogs}>
            <div className={style.dialogsItems}>
                <div className={`${style.dialog} ${style.active}`} >{DialogsElements}</div>
            </div>
            <div className={style.messages}>
                <div className = {style.message}>{MessagesElements}</div>
            </div>
        </div>
    );
}

export default Dialogs;
