import {followChangeAC, setCurrentPageAC, setTotalUsersCountAC, setUsersAC} from "../../redux/users-reducer";
import * as axios from "axios";
import * as React from "react";
import Users from "./Users";
import preloader from './../../assets/images/2.gif'
const {connect} = require("react-redux");

class UsersC extends React.Component {


    componentDidMount() {
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${this.props.currentPage}&count=${this.props.pageSize}`).then(response => {

            this.props.setUsers(response.data.items);
            this.props.setTotalUsersCount(response.data.totalCount);

        })
    }

    onPageChanged = (p) =>{
        this.props.setCurrentPage(p)
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${p}&count=${this.props.pageSize}`).then(response => {

            this.props.setUsers(response.data.items);
        })
    }

    render() {

        return (
            <>
                <img src = {this.props.isFetching? preloader: null}></img>
                <Users totalUsersCount = {this.props.totalUsersCount}
                       pageSize ={this.props.pageSize}
                       currentPage ={this.props.currentPage}
                       onPageChanged = {this.onPageChanged}
                       users ={this.props.users}
                       followChanged ={this.props.followChanged}
                       isFetching ={this.props.isFetching}
            />
            </>
        )
    }
}

let mapStateToProps = (state) =>{

    return{
        users:state.usersPage.users,
        pageSize:state.usersPage.pageSize,
        totalUsersCount: state.usersPage.totalUsersCount,
        currentPage: state.usersPage.currentPage,
        isFetching: state.usersPage.isFetching
    }
}

let mapDispatchToProps = (dispatch) =>{
    return{
        followChanged: (userID) =>{
           dispatch(followChangeAC(userID))
        },
        setUsers:(users) =>{
            dispatch(setUsersAC(users))
        },
        setCurrentPage:(currentPage) =>{
            dispatch(setCurrentPageAC(currentPage))
        },
        setTotalUsersCount:(totalUsersCount) =>{
            dispatch(setTotalUsersCountAC(totalUsersCount))
        }
    }
}

const UsersContainer = connect(mapStateToProps,mapDispatchToProps)(UsersC)
export default UsersContainer;
