import * as React from "react";

class ProfileStatus extends React.Component {
    state = {
        editMode:false
    }

    render() {
        return <div>
            {this.state.editMode ?<div onClick={this.state.editMode = !this.state.editMode}>{this.props.status}</div> :
            <input onClick={this.state.editMode = !this.state.editMode} value={this.props.status}/>}
        </div>
    }
}

export default ProfileStatus;
