export const UPDATE_NEW_MESSAGE_BODY = 'UPDATE-NEW-MESSAGE-BODY'
export const SEND_MESSAGE = 'SEND-MESSAGE'

export let sendMessageActionCreator = () =>{
    return{
        type: SEND_MESSAGE
    }
}

export let updateNewMessageBody = (newMessage) =>{
    return{
        type: UPDATE_NEW_MESSAGE_BODY,
        body:newMessage
    }
}

let initialState ={
    dialogs: [
        {message: 'hi!', id: 1},
        {message: 'hello!', id:2},
        {message: 'how ar u?', id:3},
        {message: 'good! what bout u?', id:4}],
    profiles: [
        {name: 'Alexey', profile_id: '1'},
        {name: 'Sasha', profile_id: '2'},
        {name: 'Anton', profile_id: '3'},
        {name: 'Valera', profile_id: '4'},
    ],
    newMessageBody: 'value 123'
}

const messagesReducer = (state, action) => {
    switch (action.type) {
        case SEND_MESSAGE:
        {

            state.dialogs.push({message: state.newMessageBody, id: 5});
            state.newMessageBody = '';
            return state;
        }
        case UPDATE_NEW_MESSAGE_BODY:
        {
            state.newMessageBody = action.body;
            return state;
        }
        default:
            return state;
    }

}

export default messagesReducer;
