import styles from "./ProfileInfo.module.css"
import Preloader from "../../common/Preloader/Preloader";
import ProfileStatus from "./ProfileData/ProfileStatus/ProfileStatus";
import ProfileStatusWithHooks from "./ProfileData/ProfileStatus/ProfileStatusWithHooks";
import userPhoto from './../../../assets/images/1.png'
import ProfileData from "./ProfileData/ProfileData";

const ProfileInfo = (props) => {
    if(!props.profile)
    {
        return <Preloader />
    }
    else {

        let onUserPhotoAvatarChange = (e) =>{
            if(e.target.files.length>0)
            {
                props.savePhoto(e.target.files[0])
            }
        }

        return (
            <div>
                <div className={styles.descriptionBlock}>
                    <div className={styles.profileAvatar}>
                    <img src={props.profile.photos.large || userPhoto} />
                    </div>
                    <div>
                        {
                            props.isOwner?<input type={'file'} onChange={onUserPhotoAvatarChange}/>:''
                        }
                    </div>
                    <ProfileData profile = {props.profile}/>
                </div>
            </div>
        );
    }
}

export default ProfileInfo;
