import React from 'react';
import userAvatar from './../../assets/images/1.jpg';
import styles from './Users.module.css';
import {NavLink} from "react-router-dom";
import * as axios from "axios";

const Users = (props) => {

    let pageCount = Math.ceil(props.totalUsersCount/props.pageSize);

    let pages = [];

    for(let i = 1; i <pageCount+1;i++)
    {
        pages.push(i)
    }
    return (
        <div>
            {pages.map(p =>{
                return <span className={props.currentPage === p && styles.selectedPage}  onClick={(e) =>{props.onPageChanged(p)}}>{p} </span>
            })}
            {
                props.users.map((u) => <div key={u.id}>
            <span>
                <div>
                    <NavLink to={'/profile/'+u.id}>
                    <img className={styles.smallUsersAvatar}
                         src={u.photos.small != null ? u.photos.small : userAvatar}></img>
                        </NavLink>
                </div>
                <div>
                    {u.followed ? <button onClick={() => {
                        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${this.props.currentPage}&count=${this.props.pageSize}`).then(response => {

                            this.props.setUsers(response.data.items);
                            this.props.setTotalUsersCount(response.data.totalCount);
                            this.props.toggleIsFetching(false);
                        })
                        props.followChanged(u.id)

                    }}>Unfollow</button> : <button onClick={() => {

                        props.followChanged(u.id)

                    }}>Follow</button>}
                </div>
            </span>
                        <span>
                    <div>{u.name}</div><div>{u.status}</div>
                    <div>Ukraine</div><div>Odessa</div>
            </span>
                    </div>
                )
            }
        </div>
    )
}

export default Users;
