import * as React from "react";
import styles from './ProfileStatus.module.css'

class ProfileStatus extends React.Component {

    state = {
        editMode:false,
        status:this.props.status
    }

    changeEditMode  = () =>{

        this.setState({
            editMode:!this.state.editMode
        })
        this.props.postUserStatus(this.state.status)
    }

    onStatusChange = (e) =>{
        this.setState(
            {
                status: e.currentTarget.value
            }
        )
    }

    render() {
        return <div className={styles.profileStatus}>
            {this.state.editMode ? <input  onChange = {this.onStatusChange}  autoFocus={true} onBlur={this.activateEditMode} value={this.state.status}/>:
            <div  onDoubleClick={this.deactivateEditMode} >{this.state.status}</div> }
        </div>
    }
}

export default ProfileStatus;
