import {Field, reduxForm} from "redux-form";
import {Input, Textarea} from "../../../common/FormControls/FormControls";
import {requiredFields} from "../../../../utils/validators/validators";
import styles from "../../../common/FormControls/FormControls.module.css";
import ProfileStatusWithHooks from "./ProfileStatus/ProfileStatusWithHooks";
import Contact from "./Contact/Contact";

const ProfileDataForm = (props) => {
    return <form onSubmit={props.handleSubmit}>
        <div>
            <Field name = 'fullName'>{props.profile.fullName}</Field>
        </div>
        <div>
                <Field component={'input'} name={'lookingForAJob'} type={"checkbox"}/> Looking for a job
        </div>
        <div>

            <b>Status:</b><ProfileStatusWithHooks status={props.status} postUserStatus={props.postUserStatus}
                                                  disable={props.disable}/>
        </div>
        <div>
            <b>About me:</b><Field name = 'aboutMe' component={Textarea} placeholder={props.profile.aboutMe}></Field>
        </div>
        <div>
            {
                Object.keys(props.profile.contacts).map(k => {
                    return(
                        <span><b>{k}:</b><Field name = {k} component={Input} placeholder={props.profile.contacts[k]}></Field></span>)
                })
            }
        </div>
        <div>
            <button>Apply changes</button>
        </div>
    </form>

}

const ProfileDataReduxForm = reduxForm({
    form:'profile'
})(ProfileDataForm)


let ProfileData = (props) =>{
    const onSubmit = (formData) =>{
        props.saveProfile(formData);
        props.deactivateEditMode();
    }
    return<ProfileDataReduxForm {...props} onSubmit ={onSubmit}/>
}


export default ProfileData
