import React from 'react';
import * as axios from "axios";
import userAvatar from './../../assets/images/1.jpg';
import styles from './Users.module.css';

const Users = (props) => {

    if(props.users.length===0) {

        axios.get('https://social-network.samuraijs.com/api/1.0/users').then(response =>{

            props.setUsers(response.data.items);
        })


    }
    return (
        <div>
            {
                props.users.map((u) => <div key={u.id}>
            <span>
                <div className={styles.smallUsersAvatar}>
                    <img src={u.photos.small != null ? u.photos.small: userAvatar}></img>
                </div>
                <div>
                    {u.followed ? <button onClick={() =>{props.followChanged(u.id)}}>Followed</button> : <button onClick={() =>{props.followChanged(u.id)}}>Unfollowed</button>}
                </div>
            </span>
                        <span>
                    <div>{u.name}</div><div>{u.status}</div>
                    <div>Ukraine</div><div>Odessa</div>
            </span>
                    </div>
                )
            }
        </div>
    )
}

export default Users;
