import {profileAPI} from "../api/api";

export const ADD_POST = 'ADD-POST'
export const UPDATE_NEW_POST_TEXT = 'UPDATE-NEW-POST-TEXT'
export const SET_USER_PROFILE = 'SET-USER-PROFILE'
export const GET_USER_STATUS = 'GET-USER-STATUS'

let initialState = {
    posts: [
        {id: 1, message: 'My Last post!', likes_count: 22},
        {id: 2, message: 'My First post!', likes_count: 10}
    ],
    profile: null,
    status: "ALOXA"
};

const profileReducer = (state = initialState, action) => {
    switch (action.type) {
        case ADD_POST: {
            let newPost = {
                id: 3,
                message: action.post,
                likes_count: 1
            };
            let stateCopy = {...state};
            stateCopy.posts = [...state.posts];
            stateCopy.posts.push(newPost)
            stateCopy.newPostText = '';
            return stateCopy;
        }

        case  UPDATE_NEW_POST_TEXT: {
            let stateCopy = {...state};

            stateCopy.newPostText = action.newText;
            return stateCopy;
        }
        case SET_USER_PROFILE: {
            return {...state, profile: action.profile}
        }
        case GET_USER_STATUS: {

            return {...state, status: action.status}
        }
        default:
            return state;
    }
}

export let addPostActionCreator = (post) => {

    return {
        type: ADD_POST,
        post
    }
};

export let updateNewPostTextActionCreator = (newText) => {
    return {
        type: UPDATE_NEW_POST_TEXT,
        newText: newText
    }
};

export let setUserProfile = (profile) => {

    return {
        type: SET_USER_PROFILE,
        profile
    }
}


export let getUserStatus = (status) => {
    return {
        type: GET_USER_STATUS,
        status
    }
}

export let getUserStatusThunkCreator = (userID) => {
    return (dispatch) => {
        profileAPI.getUsersStatus(userID).then(response => {
                dispatch(getUserStatus(response.data))
            }
        )
    }
}

export let postUserStatusThunkCreator = (status) => (dispatch) => {
    profileAPI.postUsersStatus(status).then(response => {
            dispatch(getUserStatus(status))
        }
    )
}


export const setUserProfileThunkCreator = (userID) => {
    return (dispatch) => {

        profileAPI.setUserProfile(userID).then(response => {

            dispatch(setUserProfile(response.data));

        })
    }
}


export default profileReducer;
