import * as React from "react";
import Header from "./Header";
import {logoutThunkCreator, setAuthUserData, setAuthUserThunkCreator} from "../../redux/auth-reducer";
import {connect} from "react-redux";


class HeaderContainer extends React.Component {

    componentDidMount() {
       this.props.setAuthUserThunkCreator();
    }

    render() {
        return <Header {...this.props}/>
    }
}

let mapStateToProps = (state) =>({
        isAuth: state.auth.isAuth,
        login: state.auth.login
})

export default connect(mapStateToProps,{setAuthUserData, setAuthUserThunkCreator,
logoutThunkCreator})(HeaderContainer)
