const ADD_POST = 'ADD-POST'
const UPDATE_NEW_POST_TEXT = 'UPDATE-NEW-POST-TEXT'

export let addPostActionCreator = () => {

    return {
        type: ADD_POST
    }
};

export let updateNewPostTextActionCreator = (newText) => {
    return {
        type: UPDATE_NEW_POST_TEXT,
        newText :newText
    }
};

let store = {
    _state: {
        messagesPage: {
            dialogs: [
                {message: 'hi!'},
                {message: 'hello!'},
                {message: 'how ar u?'},
                {message: 'good! what bout u?'}],
            profiles: [
                {name: 'Alexey', profile_id: '1'},
                {name: 'Sasha', profile_id: '2'},
                {name: 'Anton', profile_id: '3'},
                {name: 'Valera', profile_id: '4'},
            ]
        },
        profilePage: {
            posts: [
                {id: 1, message: 'My Last post!', likes_count: 22},
                {id: 2, message: 'My First post!', likes_count: 10}
            ],

            newPostText: 'value 123'
        }
    },
     _callSubscriber() {
        console.log("call Subscriber");
    },
    getState()
    {
        return this._state;
    },
    subscribe (observer){
        this._callSubscriber = observer;
    },
    dispatch(action){
        if(action.type === ADD_POST)
        {
            let newPost = {
                id: 3,
                message: this._state.profilePage.newPostText,
                likes_count: 1
            };
            this._state.profilePage.posts.push(newPost);
            this._callSubscriber(this._state);
            this._state.profilePage.newPostText = '';
        }
        if(action.type === UPDATE_NEW_POST_TEXT)
        {
            this._state.profilePage.newPostText = action.newText;
            this._callSubscriber(this._state);
        }
    }

}


export default store;
