import Profile from "./Profile";
import * as React from "react";
import * as axios from "axios";
import {connect} from "react-redux";
import {setUserProfile, setUserProfileThunkCreator} from "../../redux/profile-reducer";
import {withRouter} from "react-router";
import {profileAPI} from "../../api/api";


class ProfileContainer extends React.Component{
    componentDidMount() {
        let userID = this.props.match.params.userID;
        !userID?userID="2":userID=userID;
        profileAPI.setUserProfile(userID).then(response => {

            this.props.setUserProfile(response.data);

        })
    }

    render(){
        return<div>
        <Profile {...this.props} profile = {this.props.profile}/>
        </div>
    }
}

let mapStateToProps = (state)=>({
    profile: state.profilePage.profile
})

let WithUrlComponentContainer = withRouter(ProfileContainer);

export default connect(mapStateToProps,{
    setUserProfile,
    setUserProfileThunkCreator
})(WithUrlComponentContainer);
