import {observe} from "web-vitals/dist/lib/observe";

let rerenderEntireTree = () => {

}

let store = {
    _state: {
        messagesPage: {


            dialogs: [
                {message: 'hi!'},
                {message: 'hello!'},
                {message: 'how ar u?'},
                {message: 'good! what bout u?'}],
            profiles: [
                {name: 'Alexey', profile_id: '1'},
                {name: 'Sasha', profile_id: '2'},
                {name: 'Anton', profile_id: '3'},
                {name: 'Valera', profile_id: '4'},
            ]
        },
        profilePage: {
            posts: [
                {id: 1, message: 'My Last post!', likes_count: 22},
                {id: 2, message: 'My First post!', likes_count: 10}
            ],

            newPostText: 'value 123'
        }
    },
     rerenderEntireTree() {

    },
    addPost(postMessage){
        let newPost = {
            id: 3,
            message: postMessage,
            likes_count: 1
        };
        this._state.profilePage.posts.push(newPost);
        this.rerenderEntireTree();
        this._state.profilePage.newPostText = '';
    },
    updateNewPostText(newText){
        this._state.profilePage.newPostText = newText;
        this.rerenderEntireTree();
    },
    subscribe (observer){
        this.rerenderEntireTree = observer;
    },
    getState()
    {
        return this._state;
    }
}




export default store;
