import * as React from "react";
import Header from "./Header";
import * as axios from "axios";
import {setAuthUserData, setAuthUserThunkCreator} from "../../redux/auth-reducer";
import {connect} from "react-redux";
import classes from './Header.module.css';
import {authAPI} from "../../api/api";


class HeaderContainerC extends React.Component {

    componentDidMount() {
       this.props.setAuthUserThunkCreator();
    }

    render() {
        return<div>

            <Header {...this.props}/>
        </div>
    }
}

let mapStateToProps = (state) =>({
        isAuth: state.auth.isAuth,
        login: state.auth.login

})

const   HeaderContainer = connect(mapStateToProps,{setAuthUserData, setAuthUserThunkCreator})(HeaderContainerC)

export default HeaderContainer;
