export const CHANGE_FOLLOW_STATUS = 'CHANGE-FOLLOW-STATUS';
export const SET_USERS = 'SET-USERS';

let initialState = {
    users: [
        {
            id: 1, followed: true, fullName: 'Eugeniy', status: 'Hey!',
            location: {
                city: 'Odessa',
                country: 'Ukraine'
            },
            photoUrl: 'https://st3.depositphotos.com/15648834/17930/v/600/depositphotos_179308454-stock-illustration-unknown-person-silhouette-glasses-profile.jpg'
        },
        {
            id: 2, followed: false, fullName: 'Sasha', status: 'Igor ne ochen :)!',
            location: {
                city: 'Minsk',
                country: 'Belarus'
            },
            photoUrl: 'https://st3.depositphotos.com/15648834/17930/v/600/depositphotos_179308454-stock-illustration-unknown-person-silhouette-glasses-profile.jpg'
        },
        {
            id: 3, followed: true, fullName: 'Igor', status: 'Vse govno!',
            location: {
                city: 'Dublin',
                country: 'Ierland'
            },
            photoUrl: 'https://st3.depositphotos.com/15648834/17930/v/600/depositphotos_179308454-stock-illustration-unknown-person-silhouette-glasses-profile.jpg'
        }
    ]
}


const usersReducer = (state = initialState, action) => {
    switch (action.type) {
        case CHANGE_FOLLOW_STATUS: {
            let stateCopy = {
                ...state,
                users: state.users.map(u => {
                    if (u.id === action.userID) {
                        return {...u, followed: !u.followed}
                    }
                }),
                ...state.users.location
            }
            return  stateCopy;
        }
        case SET_USERS:{
            return {...state, users: [state.users, ...action.users]}}
        default:
            return state;
    }
}

export const followChangeAC = (userID) => ({type:CHANGE_FOLLOW_STATUS, userID})
export const setUsersAC = (users) => ({type:SET_USERS, users})

export default usersReducer;
