import Profile from "./Profile";
import * as React from "react";
import {connect} from "react-redux";
import {getUserStatusThunkCreator, setUserProfileThunkCreator,postUserStatusThunkCreator} from "../../redux/profile-reducer";
import {withRouter} from "react-router";
import {compose} from "redux";


class ProfileContainer extends React.Component{
    componentDidMount() {
        let userID = this.props.match.params.userID;
        !userID?userID=this.props.id:userID=userID;
        this.props.setUserProfileThunkCreator(userID);
        this.props.getUserStatusThunkCreator(userID);

    }

    render(){
        return<div>
        <Profile {...this.props} profile = {this.props.profile} status = {this.props.status} postUsersStatus ={this.props.postUserStatusThunkCreator}/>
        </div>
    }
}

let mapStateToProps = (state)=>({
    profile: state.profilePage.profile,
    status : state.profilePage.status,
    id: state.auth.userID,
    isAuth:state.auth.isAuth

})



export default compose(
    connect(mapStateToProps,{
        setUserProfileThunkCreator,
        getUserStatusThunkCreator,
        postUserStatusThunkCreator
    }),
    withRouter

)(ProfileContainer)
