import * as React from "react";
import styles from "./Users.module.css";
import userAvatar from "../../assets/images/1.jpg";
import * as axios from "axios";


class Users extends React.Component {


    componentDidMount() {
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${this.props.currentPage}&count=${this.props.pageSize}`).then(response => {

            this.props.setUsers(response.data.items);
        })
    }

    onPageChanged = (p) =>{
        this.props.setCurrentPage(p)

    }

    render() {

        let pageCount = Math.ceil(this.props.totalUsersCount/this.props.pageSize);

        let pages = [];

        for(let i = 1; i <pageCount+1;i++)
        {
            pages.push(i)
        }

        return (

            <div>
                {pages.map(p =>{
                   return <span className={this.props.currentPage === p && styles.selectedPage}  onClick={(e) =>{this.onPageChanged}}>{p} </span>
                })}

                {
                    this.props.users.map((u) => <div key={u.id}>
            <span>
                <div>
                    <img className={styles.smallUsersAvatar}
                         src={u.photos.small != null ? u.photos.small : userAvatar}></img>
                </div>
                <div>
                    {u.followed ? <button onClick={() => {
                        this.props.followChanged(u.id)
                    }}>Followed</button> : <button onClick={() => {
                        this.props.followChanged(u.id)
                    }}>Unfollowed</button>}
                </div>
            </span>
                            <span>
                    <div>{u.name}</div><div>{u.status}</div>
                    <div>Ukraine</div><div>Odessa</div>
            </span>
                        </div>
                    )
                }
            </div>
        )
    }
}

export default Users;
