import styles from "./ProfileInfo.module.css"
import Preloader from "../../common/Preloader/Preloader";

const ProfileInfo = (props) => {
    if(!props.profile)
    {
        return <Preloader />
    }
    else {
        let contactsKeys =  Object.keys(props.profile.contacts);
        let contactsValues =  Object.values(props.profile.contacts);
        return (
            <div>
                <div>
                    <img
                        src='https://helpx.adobe.com/content/dam/help/en/stock/how-to/visual-reverse-image-search/jcr_content/main-pars/image/visual-reverse-image-search-v2_intro.jpg'/>
                </div>
                <div className={styles.descriptionBlock}>
                    <div >
                    <img src={props.profile.photos.large} className={styles.profileAvatar}/>
                    </div>
                    <div>
                        {props.profile.fullName}
                    </div>
                    <div>
                        <span>
                        {
                            contactsKeys.map(k =>{
                                return<div>{k}: <a href = {contactsValues[contactsKeys.indexOf(k)]}>{contactsValues[contactsKeys.indexOf(k)]}</a></div>
                            })

                        }
                        </span>
                    </div>
                </div>
            </div>
        );
    }
}

export default ProfileInfo;
